package doWhileLoop;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 1;
		
		do {
			
			System.out.println("Today is Wednesday");
			i++;
		}
		
		while(i<=3);
		
		System.out.println("print the messsages. I am out of loop");


	}

}
