package forLoop;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=10;i++)
		{
			System.out.println("the value is : "+i);
		}
	    
	    for(int i=100;i>=90;i--)
		{
			System.out.println("the value is : "+i);
		}
	    
	    for(int i=1;i<=2;++i)
		{
			System.out.println("the value is : "+i);
		}
		
		System.out.println("Reached the limit");

	}

}
